INSERT INTO `bollettini_modelli` VALUES (0,'896 generico','896','AUT. DB/SS1C/E6805/DEL 01/03/02','/bollettino896Report.jasper','/logo/giuntieditore_logo_vert.jpg',' ',NULL,NULL),(1,'896 NDD generico','896','AUT. DB/SS1C/E6805/DEL 01/03/02','/ndd896Report.jasper','/logo/giuntieditore_logo_vert.jpg',' ',NULL,NULL),(2,'896 Nuovo','896','AUT. DB/SS1C/E6799/DEL 01/03/02','/bollettino896Report.jasper','/logo/logo_periodico_B.jpg','Caro Lettore,                                                    \r\nbenvenuto nella grande famiglia degli abbonati a \"PSICOLOGIA     \r\nCONTEMPORANEA\". Abbonandosi ha senza dubbio compiuto la scelta   \r\nmigliore. Il suo nominativo e\' stato registrato ed abbiamo gia\'  \r\niniziato a inviarle con regolarita\' la rivista che periodicamente\r\ngiungera\' al suo indirizzo.                                      \r\nUnito alla presente trova un bollettino di Conto corrente Postale\r\npersonalizzato. La preghiamo di verificare l\'esattezza dei suoi  \r\ndati anagrafici, provvedendo a correggerli ove occorresse, e di  \r\neffettuare al piu\' presto il versamento in modo da rendere il suo\r\nabbonamento operativo a tutti gli effetti.                       \r\nLa informiamo che adesso e\' possibile versare la quota di abbo-  \r\nnamento con carta di credito, in completa sicurezza, sul nuovo   \r\nsito dedicato agli abbonati Giunti: www.giuntiabbonamenti.it     \r\nRingraziandola per la fiducia, le auguriamo buona lettura e le   \r\ninviamo con l\'occasione i nostri piu\' cordiali saluti.           \r\n                                         Stefano Pinzauti        \r\n                                     Resp. Servizio Abbonati     \r\n                                                                 \r\nP.S. Per ogni chiarimento, informazione o necessita\' siamo sempre\r\na sua disposizione: per telefono al n. 199 195 525, dal lunedi\'  \r\nal venerdi\' in orario 9-18; e-mail periodici@giunti.it           ',2,NULL),(3,'896 Nuovo','896','AUT. DB/SS1C/E6801/DEL 01/03/02','/bollettino896Report.jasper','/logo/logo_periodico_D.jpg','Gentile Abbonato,                                                \r\nabbiamo ricevuto la sua richiesta e siamo lieti di darle il      \r\nbenvenuto nella grande famiglia di PSICOLOGIA E SCUOLA.          \r\nAbbonandosi ha senza dubbio compiuto la scelta migliore. Il suo  \r\nnominativo e\' stato registrato e certamente avra\' gia\' iniziato a\r\nricevere la rivista con regolarita\'.                             \r\nL\'offerta di abbonamento a PSICOLOGIA E SCUOLA alla quale ha     \r\naderito e\' veramente unica e imperdibile:                        \r\n-un anno di Psicologia e Scuola, 5 fascicoli bimestrali di 64 pp.\r\n+1 numero monografico a forma di libro \"Motivarsi e Motivare\";   \r\n+l\'accesso gratuito all\'area riservata di psicologiaescuola.it   \r\n dove trovera\' questionari, test, schede di valutazione e altro  \r\n+2 fantastici libro in regalo: \"La Scrittura e i suoi errori\"    \r\ndi Carlo Pascoletti e \"La creativita\' si impara\" di Alessandro   \r\nAntonietti.                                                      \r\nTutto questo a soli euro 24,00.                                  \r\nA questo punto non le resta che regolarizzare subito il suo      \r\nabbonamento utilizzando il bollettino di conto corrente postale  \r\nqui sotto oppure on-line, con carta di credito in completa       \r\nsicurezza sul nuovo sito dedicato agli abbonati Giunti:          \r\n                  www.giuntiabbonamenti.it                       \r\n                                              Buona lettura!     ',4,NULL),(4,'896 Scuola dell\'Infanzia','896','AUT. DB/SS1C/E6803/DEL 01/03/02','/bollettino896Report.jasper','/logo/logo_periodico_M.jpg','Periodici Giunti',7,NULL),(5,'896 Nuovo','896','AUT. DB/SS1C/E6802/DEL 01/03/03','/bollettino896Report.jasper','/logo/logo_periodico_Q.jpg','Caro Lettore,                                                    \r\nbenvenuto nella grande famiglia degl